	public static function retrieveByPK($pru_fecha_evalucion, $his_id_asociada, $usu_evaluador_id, PropelPDO $con = null) {
		$key = serialize(array((string) $pru_fecha_evalucion, (string) $his_id_asociada, (string) $usu_evaluador_id));
 		if (null !== ($obj = AgilhuPruebaExpertoPeer::getInstanceFromPool($key))) {
