		}
		$criteria = new Criteria(AgilhuPruebaExpertoPeer::DATABASE_NAME);
		$criteria->add(AgilhuPruebaExpertoPeer::PRU_FECHA_EVALUCION, $pru_fecha_evalucion);
		$criteria->add(AgilhuPruebaExpertoPeer::HIS_ID_ASOCIADA, $his_id_asociada);
		$criteria->add(AgilhuPruebaExpertoPeer::USU_EVALUADOR_ID, $usu_evaluador_id);
