	public function setByPosition($pos, $value)
	{
		switch($pos) {
			case 0:
				$this->setIdUsuario($value);
				break;
			case 1:
				$this->setUsuUsuario($value);
				break;
			case 2:
				$this->setUsuClave($value);
				break;
			case 3:
				$this->setUsuNombres($value);
				break;
			case 4:
				$this->setUsuApellidos($value);
				break;
			case 5:
				$this->setUsuCorreo($value);
				break;
			case 6:
				$this->setUsuRolId($value);
				break;
			case 7:
				$this->setUsuEstado($value);
				break;
		} // switch()
	}

	/**
	 * Populates the object using an array.
	 *
	 * This is particularly useful when populating an object from one of the
	 * request arrays (e.g. $_POST).  This method goes through the column
	 * names, checking to see whether a matching key exists in populated
	 * array. If so the setByName() method is called for that column.
	 *
	 * You can specify the key type of the array by additionally passing one
	 * of the class type constants BasePeer::TYPE_PHPNAME, BasePeer::TYPE_STUDLYPHPNAME,
	 * BasePeer::TYPE_COLNAME, BasePeer::TYPE_FIELDNAME, BasePeer::TYPE_NUM.
	 * The default key type is the column's phpname (e.g. 'AuthorId')
	 *
	 * @param      array  $arr     An array to populate the object from.
	 * @param      string $keyType The type of keys the array uses.
	 * @return     void
	 */
	public function fromArray($arr, $keyType = BasePeer::TYPE_PHPNAME)
	{
		$keys = UsuarioPeer::getFieldNames($keyType);

		if (array_key_exists($keys[0], $arr)) $this->setIdUsuario($arr[$keys[0]]);
		if (array_key_exists($keys[1], $arr)) $this->setUsuUsuario($arr[$keys[1]]);
		if (array_key_exists($keys[2], $arr)) $this->setUsuClave($arr[$keys[2]]);
		if (array_key_exists($keys[3], $arr)) $this->setUsuNombres($arr[$keys[3]]);
		if (array_key_exists($keys[4], $arr)) $this->setUsuApellidos($arr[$keys[4]]);
		if (array_key_exists($keys[5], $arr)) $this->setUsuCorreo($arr[$keys[5]]);
		if (array_key_exists($keys[6], $arr)) $this->setUsuRolId($arr[$keys[6]]);
		if (array_key_exists($keys[7], $arr)) $this->setUsuEstado($arr[$keys[7]]);
	}

	/**
	 * Build a Criteria object containing the values of all modified columns in this object.
	 *
	 * @return     Criteria The Criteria object containing all modified values.
	 */
	public function buildCriteria()
	{
		$criteria = new Criteria(UsuarioPeer::DATABASE_NAME);

		if ($this->isColumnModified(UsuarioPeer::ID_USUARIO)) $criteria->add(UsuarioPeer::ID_USUARIO, $this->id_usuario);
		if ($this->isColumnModified(UsuarioPeer::USU_USUARIO)) $criteria->add(UsuarioPeer::USU_USUARIO, $this->usu_usuario);
		if ($this->isColumnModified(UsuarioPeer::USU_CLAVE)) $criteria->add(UsuarioPeer::USU_CLAVE, $this->usu_clave);
		if ($this->isColumnModified(UsuarioPeer::USU_NOMBRES)) $criteria->add(UsuarioPeer::USU_NOMBRES, $this->usu_nombres);
		if ($this->isColumnModified(UsuarioPeer::USU_APELLIDOS)) $criteria->add(UsuarioPeer::USU_APELLIDOS, $this->usu_apellidos);
		if ($this->isColumnModified(UsuarioPeer::USU_CORREO)) $criteria->add(UsuarioPeer::USU_CORREO, $this->usu_correo);
		if ($this->isColumnModified(UsuarioPeer::USU_ROL_ID)) $criteria->add(UsuarioPeer::USU_ROL_ID, $this->usu_rol_id);
		if ($this->isColumnModified(UsuarioPeer::USU_ESTADO)) $criteria->add(UsuarioPeer::USU_ESTADO, $this->usu_estado);

		return $criteria;
	}

	/**
	 * Builds a Criteria object containing the primary key for this object.
	 *
	 * Unlike buildCriteria() this method includes the primary key values regardless
	 * of whether or not they have been modified.
	 *
	 * @return     Criteria The Criteria object containing value(s) for primary key(s).
	 */
	public function buildPkeyCriteria()
	{
		$criteria = new Criteria(UsuarioPeer::DATABASE_NAME);

		$criteria->add(UsuarioPeer::ID_USUARIO, $this->id_usuario);

		return $criteria;
	}

	/**
